import java.util.LinkedList;

public class LinkList {
    public static void main(String[] args){
        LinkedList II=new LinkedList();
        II.add("H");
        II.add("E");
        II.add("L");
        II.add("L");
        II.add("O");
        II.addLast("");
        II.addLast("CHEV");
        II.addFirst("I");
        II.add(1,"");
        System.out.println("Original content of II  :  "+II);
        II.remove("CHEV");
        II.remove(1);
        System.out.println("Content of II after delete  :  "+II);
        II.removeFirst();
        II.removeLast();
        System.out.println("Content of II after remove first and last  :  "+II);
        Object Val=II.get(2);
        II.set(2,(String)Val + " ");
        System.out.println("II after changed  :  "+II);
    }
}
